mapper.select:
select
id, parent_id, name, path, perms, component, type, icon, orderNum, created, updated, state
from vueadmin.sys_menu


        and id = #{id}


        and parent_id = #{parentId}


        and name = #{name}


        and path = #{path}


        and perms = #{perms}


        and component = #{component}


        and type = #{type}


        and icon = #{icon}


        and orderNum = #{ordernum}


        and created = #{created}


        and updated = #{updated}


        and state = #{state}
